import './link.scss'


type IconT = 'add' | 'share' | 'redo' | 'undo' | 'home'
type LinkT = {
	to: string,
	icon: IconT,
	name: string
}

const getIcon = {
	add: <img src="data:image/svg+xml;base64,"/>,
	share: <img src="data:image/svg+xml;base64,"/>,
	redo:<img src="data:image/svg+xml;base64,"/> ,
	undo: <img src="data:image/svg+xml;base64,"/>,
	home:<img src="data:image/svg+xml;base64,"/> }

const Link = (e: LinkT) => {
	return (
		<a href={e.to}>
			{getIcon[e.icon]}
			<br />
			{e.name}
		</a>
	)
}

export default Link;
